    'ts-jest': {
      useESM: true,
    },
  },
};

import { jest, describe, it, expect, afterEach, beforeEach } from '@jest/globals';

const mockSend = jest.fn().mockResolvedValue({});
jest.doMock("resend", () => ({
  Resend: jest.fn().mockImplementation(() => ({
    emails: {
      send: mockSend,
    },
  })),
}));

import { appRouter } from "~/server/api/root";
import { createCallerFactory } from "~/server/api/trpc";
import { db } from "~/server/db";
import * as bcrypt from "bcryptjs";

describe("authRouter", () => {
  beforeEach(() => {
    mockSend.mockClear();
  });

  afterEach(async () => {
    await db.user.deleteMany({});
    await db.verificationToken.deleteMany({});
  });

  it("should register a new user, hash the password, and send a verification email", async () => {
    const createCaller = createCallerFactory(appRouter);

    const caller = createCaller({ db });
    const input = {
      email: "test@example.com",
      password: "password123",
    };

    const result = await caller.auth.register(input);

    expect(result.message).toBe("Registration successful. Please check your email for verification.");
    expect(result.user.email).toBe(input.email);

    const userInDb = await db.user.findUnique({ where: { email: input.email } });
    expect(userInDb).not.toBeNull();
    if (userInDb) {
      expect(userInDb.email).toBe(input.email);
      const passwordMatch = await bcrypt.compare(input.password, userInDb.passwordHash);
      expect(passwordMatch).toBe(true);
    }

    const verificationToken = await db.verificationToken.findFirst({ where: { identifier: input.email } });
    expect(verificationToken).not.toBeNull();

    expect(mockSend).toHaveBeenCalledTimes(1);
  });
});

