import { z } from "zod";
import { createTRPCRouter, publicProcedure } from "../trpc";
import { db } from "~/server/db";
import * as bcrypt from "bcryptjs";
import { Resend } from "resend";
import { env } from "~/env";

const resend = new Resend(env.RESEND_API_KEY);

export const authRouter = createTRPCRouter({
  register: publicProcedure
    .input(z.object({
      email: z.string().email(),
      password: z.string().min(8),
    }))
    .mutation(async ({ ctx, input }) => {
      const { email, password } = input;

      const existingUser = await db.user.findUnique({
        where: { email },
      });

      if (existingUser) {
        throw new Error("User with this email already exists.");
      }

      const hashedPassword = await bcrypt.hash(password, 10);

      const newUser = await db.user.create({
        data: {
          email,
          passwordHash: hashedPassword,
        },
      });

      // Use the new token generation utility
      const { generateVerificationToken } = await import("~/server/utils/token");
      const { token: plainToken, hashedToken, expires } = generateVerificationToken();

      await db.verificationToken.create({
        data: {
          userId: newUser.id,
          hashedToken,
          expires,
        },
      });

      // Construct verification URL pointing to the tRPC endpoint
      // Ensure the URL is correctly formatted for tRPC calls with input
      const verificationUrl = `${env.NEXTAUTH_URL}/api/trpc/auth.verifyEmail?input=${encodeURIComponent(JSON.stringify({ token: plainToken }))}`;

      await resend.emails.send({
        from: "onboarding@resend.dev",
        to: email,
        subject: "Verify your email address",
        html: `<p>Click <a href="${verificationUrl}">here</a> to verify your email address.</p>`,
      });

      return {
        message: "Registration successful. Please check your email for verification.",
        user: {
          id: newUser.id,
          email: newUser.email,
        },
      };
    }),

  verifyEmail: publicProcedure
    .input(z.object({
      token: z.string().min(1, "Verification token is required."),
    }))
    .mutation(async ({ ctx, input }) => {
      const { token: plainToken } = input;
      const { verifyVerificationToken } = await import("~/server/utils/token"); // Lazy import to avoid circular dependency if token.ts depends on db

      // Hash the incoming token to match what's stored in the database
      // The stored token in the DB is the hashed version
      // The plainToken is what the user clicks in the email
      const incomingHashedToken = (await import('crypto')).createHash('sha256').update(plainToken).digest('hex');

      const verificationToken = await db.verificationToken.findUnique({
        where: {
          hashedToken: incomingHashedToken,
        },
      });

      if (!verificationToken) {
        throw new TRPCError({
          code: "UNAUTHORIZED",
          message: "Invalid or already used verification token.",
        });
      }

      if (verificationToken.expires < new Date()) {
        await db.verificationToken.delete({
          where: { id: verificationToken.id },
        });
        throw new TRPCError({
          code: "UNAUTHORIZED",
          message: "Verification token has expired. Please register again.",
        });
      }

      await db.user.update({
        where: { id: verificationToken.userId },
        data: { emailVerified: new Date() },
      });

      await db.verificationToken.delete({
        where: { id: verificationToken.id },
      });

      return { message: "Email verified successfully. You can now log in." };
    }),
});

