// This is your Prisma schema file,
// learn more about it in the docs: https://pris.ly/d/prisma-schema

generator client {
    provider = "prisma-client-js"
    output   = "../generated/prisma"
}

datasource db {
    provider = "postgresql"
    // NOTE: When using mysql or sqlserver, uncomment the @db.Text annotations in model Account below
    // Further reading:
    // https://next-auth.js.org/adapters/prisma#create-the-prisma-schema
    // https://www.prisma.io/docs/reference/api-reference/prisma-schema-reference#string
    url      = env("DATABASE_URL")
}

// Enum for Role-Based Access Control
enum Role {
  STUDENT
  TEACHER
}

// Necessary for Next auth
model Account {
    id                       String  @id @default(cuid())
    userId                   String
    type                     String
    provider                 String
    providerAccountId        String
    refresh_token            String? // @db.Text
    access_token             String? // @db.Text
    expires_at               Int?
    token_type               String?
    scope                    String?
    id_token                 String? // @db.Text
    session_state            String?
    user                     User    @relation(fields: [userId], references: [id], onDelete: Cascade)
    refresh_token_expires_in Int?

    @@unique([provider, providerAccountId])
}

model Session {
    id           String   @id @default(cuid())
    sessionToken String   @unique
    userId       String
    expires      DateTime
    user         User     @relation(fields: [userId], references: [id], onDelete: Cascade)
}

model User {
    id            String    @id @default(cuid())
    name          String?
    email         String?   @unique
    emailVerified DateTime?
    image         String?
    role          Role      @default(STUDENT)
    accounts      Account[]
    sessions      Session[]
}

model VerificationToken {
  identifier String
  token      String   @unique
  expires    DateTime

  @@unique([identifier, token])
}

import { PrismaAdapter } from "@auth/prisma-adapter";
import { type DefaultSession, type NextAuthConfig } from "next-auth";
import DiscordProvider from "next-auth/providers/discord";
import GoogleProvider from "next-auth/providers/google";
import { type Role } from "@prisma/client";

import { db } from "~/server/db";

/**
 * Module augmentation for `next-auth` types. Allows us to add custom properties to the `session`
 * object and keep type safety.
 *
 * @see https://next-auth.js.org/getting-started/typescript#module-augmentation
 */
declare module "next-auth" {
  interface Session extends DefaultSession {
    user: {
      id: string;
      role: Role;
    } & DefaultSession["user"];
  }

  interface User {
    role: Role;
  }
}

/**
 * Options for NextAuth.js used to configure adapters, providers, callbacks, etc.
 *
 * @see https://next-auth.js.org/configuration/options
 */
export const authConfig = {
  providers: [
    DiscordProvider({
      clientId: process.env.DISCORD_CLIENT_ID,
      clientSecret: process.env.DISCORD_CLIENT_SECRET,
    }),
    GoogleProvider({
      clientId: process.env.GOOGLE_CLIENT_ID,
      clientSecret: process.env.GOOGLE_CLIENT_SECRET,
    }),
    /**
     * ...add more providers here.
     *
     * Most other providers require a bit more work than the Discord provider. For example, the
     * GitHub provider requires you to add the `refresh_token_expires_in` field to the Account
     * model. Refer to the NextAuth.js docs for the provider you want to use. Example:
     *
     * @see https://next-auth.js.org/providers/github
     */
  ],
  adapter: PrismaAdapter(db),
  callbacks: {
    signIn: async ({ user, account, profile }) => {
      if (user.email) {
        // Placeholder for school email domain check
        // In a real application, this would be configured with the actual school domain(s)
        if (!user.email.endsWith("@school.com")) {
          // Optionally, redirect to an error page or show a message
          // For now, prevent sign-in for unauthorized domains
          return false;
        }
      }
      return true;
    },
    session: ({ session, user }) => ({
      ...session,
      user: {
        ...session.user,
        id: user.id,
        role: user.role,
      },
    }),
  },
} satisfies NextAuthConfig;

import NextAuth from "next-auth";
import { authConfig } from "./config";

export const { handlers, auth, signIn, signOut } = NextAuth(authConfig);

import { NextResponse } from "next/server";
import { auth } from "~/server/auth";
import { type NextRequest } from "next/server";

export default auth((req) => {
  const { nextUrl } = req;
  const session = req.auth;

  const isLoggedIn = !!session;

  const isApiAuthRoute = nextUrl.pathname.startsWith("/api/auth");
  const isTRPCRoute = nextUrl.pathname.startsWith("/api/trpc");
  const isPublicRoute = ["/"].includes(nextUrl.pathname);


  if (isApiAuthRoute || isTRPCRoute || isPublicRoute) {
    return NextResponse.next();
  }

  if (isLoggedIn) {
    const userRole = session?.user?.role;
    const isTeacherDashboard = nextUrl.pathname.startsWith("/teacher");
    const isStudentDashboard = nextUrl.pathname.startsWith("/student");

    // If logged in and at the root, redirect to the correct dashboard
    if (nextUrl.pathname === "/") {
      if (userRole === "TEACHER") {
        return NextResponse.redirect(new URL("/teacher/dashboard", nextUrl));
      } else {
        return NextResponse.redirect(new URL("/student/dashboard", nextUrl));
      }
    }

    if (isTeacherDashboard && userRole !== "TEACHER") {
      return NextResponse.redirect(new URL("/student/dashboard", nextUrl));
    }

    if (isStudentDashboard && userRole !== "STUDENT") {
      return NextResponse.redirect(new URL("/teacher/dashboard", nextUrl));
    }

    return NextResponse.next();
  }

  // Redirect to login if not logged in and trying to access a protected route
  if (!isLoggedIn && !isPublicRoute) {
    return NextResponse.redirect(new URL("/", nextUrl));
  }


  return NextResponse.next();
});

// Optionally, don't invoke Middleware on some paths
// Read more: https://nextjs.org/docs/app/building-your-application/routing/middleware#matcher
export const config = {
  matcher: ["/((?!.+\\.[\\w]+$|_next).*)", "/", "/(api|trpc)(.*)"],
};

import React from 'react';

const StudentDashboardPage = () => {
  return (
    <div className="container mx-auto py-8">
      <h1 className="text-3xl font-bold">Student Dashboard</h1>
      <p className="mt-4">Welcome to your dashboard. Here you will find your courses.</p>
      {/* Placeholder for course selection */}
    </div>
  );
};

export default StudentDashboardPage;

import React from 'react';

const TeacherDashboardPage = () => {
  return (
    <div className="container mx-auto py-8">
      <h1 className="text-3xl font-bold">Teacher Dashboard</h1>
      <p className="mt-4">Welcome to your dashboard. Here you can manage your courses and content.</p>
      {/* Placeholder for course management */}
    </div>
  );
};

export default TeacherDashboardPage;

const nextJest = require('next/jest')

const createJestConfig = nextJest({
  // Provide the path to your Next.js app to load next.config.js and .env files in your test environment
  dir: './',
})

// Add any custom config to be passed to Jest
const customJestConfig = {
  setupFilesAfterEnv: ['<rootDir>/jest.setup.js'],
  testEnvironment: 'jest-environment-jsdom',
  preset: 'ts-jest',
  moduleNameMapper: {
    '^~/(.*)$': '<rootDir>/src/$1',
  },
}

// createJestConfig is exported this way to ensure that next/jest can load the Next.js config which is async
module.exports = createJestConfig(customJestConfig)

