import fs from 'fs/promises';
import path from 'path';
import pdfParse from 'pdf-parse';
import mammoth from 'mammoth';

type FileType = 'pdf' | 'docx' | 'txt' | string; // Allowing string for future expansion

export class DocumentParser {
  async parse(filePath: string, fileType: FileType): Promise<string> {
    const absolutePath = path.resolve(filePath);

    switch (fileType) {
      case 'pdf':
        return this._parsePdf(absolutePath);
      case 'docx':
        return this._parseDocx(absolutePath);
      case 'txt':
        return this._parseTxt(absolutePath);
      default:
        throw new Error(`Unsupported file type: ${fileType}`);
    }
  }

  private async _parsePdf(filePath: string): Promise<string> {
    const dataBuffer = await fs.readFile(filePath);
    const data = await pdfParse(dataBuffer);
    return data.text;
  }

  private async _parseDocx(filePath: string): Promise<string> {
    const { value } = await mammoth.extractRawText({ path: filePath });
    return value;
  }

  private async _parseTxt(filePath: string): Promise<string> {
    return fs.readFile(filePath, 'utf-8');
  }
}

import OpenAI from 'openai';

export class EmbeddingModel {
  private openai: OpenAI;

  constructor(apiKey: string) {
    if (!apiKey) {
      throw new Error('OpenAI API key is not provided.');
    }
    this.openai = new OpenAI({ apiKey });
  }

  async generateEmbedding(text: string): Promise<number[]> {
    if (!text) {
      return [];
    }

    try {
      const response = await this.openai.embeddings.create({
        model: 'text-embedding-ada-002', // Or another suitable model
        input: text,
      });

      return response.data[0].embedding;
    } catch (error) {
      console.error('Error generating embedding:', error);
      throw new Error('Failed to generate embedding.');
    }
  }
}

import { Pinecone, PineconeRecord } from '@pinecone-database/pinecone';

export interface Vector {
  id: string;
  values: number[];
  metadata?: {
    [key: string]: string | number | boolean | Array<string | number | boolean>;
  };
}

export interface QueryResult {
  matches: Array<{
    id: string;
    score: number;
    values: number[];
    metadata: {
      [key: string]: string | number | boolean | Array<string | number | boolean>;
    };
  }>;
  namespace?: string;
}

export class PineconeService {
  private pinecone: Pinecone;
  private indexName: string;

  constructor(apiKey: string, environment: string, indexName: string) {
    if (!apiKey || !environment || !indexName) {
      throw new Error('Pinecone API key, environment, or index name not provided.');
    }

    this.pinecone = new Pinecone({
      apiKey,
      environment, // deprecated but still needed for constructor
    });
    this.indexName = indexName;
  }

  async upsertVectors(vectors: Vector[]): Promise<void> {
    const index = this.pinecone.index(this.indexName);
    const records: PineconeRecord[] = vectors.map(v => ({
      id: v.id,
      values: v.values,
      metadata: v.metadata,
    }));
    await index.upsert(records);
  }

  async queryVectors(vector: number[], topK: number): Promise<QueryResult> {
    const index = this.pinecone.index(this.indexName);
    const queryResponse = await index.query({
      vector,
      topK,
      includeMetadata: true,
      includeValues: false, // Typically you don't need values back for a query
    });

    // Map to the desired QueryResult interface
    const matches = queryResponse.matches?.map(match => ({
      id: match.id,
      score: match.score || 0, // PineconeRecord.score is optional
      values: match.values || [], // PineconeRecord.values is optional
      metadata: (match.metadata as { [key: string]: string | number | boolean | Array<string | number | boolean> }) || {},
    })) || [];

    return { matches, namespace: queryResponse.namespace };
  }
}

import { DocumentParser } from './services/parser';
import { EmbeddingModel } from './services/embedding';
import { PineconeService, Vector } from './services/pinecone';
import { getEnvVariable } from '../env'; // Assuming a utility to get validated env vars

interface Document {
  id: string;
  filePath: string;
  fileType: string; // e.g., 'pdf', 'docx', 'txt'
  metadata: {
    courseId: string;
    documentName: string;
    // Add other relevant metadata
  };
}

export class IngestionService {
  private documentParser: DocumentParser;
  private embeddingModel: EmbeddingModel;
  private pineconeService: PineconeService;

  constructor() {
    this.documentParser = new DocumentParser();
    this.embeddingModel = new EmbeddingModel(getEnvVariable('OPENAI_API_KEY')); // Using OPENAI for now
    this.pineconeService = new PineconeService(
      getEnvVariable('PINECONE_API_KEY'),
      getEnvVariable('PINECONE_ENVIRONMENT'),
      getEnvVariable('PINECONE_INDEX_NAME')
    );
  }

  // Placeholder for fetching documents from Canvas
  private async fetchDocumentsFromCanvas(courseId: string): Promise<Document[]> {
    console.log(`Fetching documents for course: ${courseId} from Canvas (placeholder)`);
    // In a real scenario, this would call the Canvas integration service (Story 2.1)
    // For now, return mock documents
    return [
      // {
      //   id: 'doc1_courseA',
      //   filePath: '/path/to/courseA_syllabus.pdf',
      //   fileType: 'pdf',
      //   metadata: { courseId: 'courseA', documentName: 'Syllabus' },
      // },
      // {
      //   id: 'doc2_courseA',
      //   filePath: '/path/to/courseA_assignment.docx',
      //   fileType: 'docx',
      //   metadata: { courseId: 'courseA', documentName: 'Assignment 1' },
      // },
    ];
  }

  async ingestCourseDocuments(courseId: string): Promise<void> {
    const documents = await this.fetchDocumentsFromCanvas(courseId);

    for (const doc of documents) {
      try {
        console.log(`Parsing document: ${doc.documentName}`);
        const textContent = await this.documentParser.parse(doc.filePath, doc.fileType);

        console.log(`Generating embedding for document: ${doc.documentName}`);
        const embedding = await this.embeddingModel.generateEmbedding(textContent);

        if (embedding.length > 0) {
          const vector: Vector = {
            id: doc.id,
            values: embedding,
            metadata: {
              ...doc.metadata,
              text: textContent.substring(0, 500), // Store first 500 chars of text for context
            },
          };

          console.log(`Upserting vector for document: ${doc.documentName}`);
          await this.pineconeService.upsertVectors([vector]);
          console.log(`Successfully ingested: ${doc.documentName}`);
        } else {
          console.warn(`No embedding generated for document: ${doc.documentName}. Skipping upsert.`);
        }
      } catch (error) {
        console.error(`Error processing document ${doc.documentName}:`, error);
        // Continue to next document even if one fails
      }
    }
    console.log(`Ingestion process for course ${courseId} completed.`);
  }
}

