import { DocumentParser } from '../parser';
import fs from 'fs/promises';
import path from 'path';
import pdfParse from 'pdf-parse';
import mammoth from 'mammoth';

// Mock the external libraries
jest.mock('fs/promises');
jest.mock('pdf-parse');
jest.mock('mammoth');

describe('DocumentParser', () => {
  let parser: DocumentParser;

  beforeEach(() => {
    parser = new DocumentParser();
    // Clear all mocks before each test
    (fs.readFile as jest.Mock).mockClear();
    (pdfParse as jest.Mock).mockClear();
    (mammoth.extractRawText as jest.Mock).mockClear();
  });

  it('should parse a TXT file correctly', async () => {
    const mockTxtContent = 'This is a test text file.';
    (fs.readFile as jest.Mock).mockResolvedValue(mockTxtContent);

    const filePath = 'path/to/mock.txt';
    const result = await parser.parse(filePath, 'txt');

    expect(result).toBe(mockTxtContent);
    expect(fs.readFile).toHaveBeenCalledWith(path.resolve(filePath), 'utf-8');
  });

  it('should parse a PDF file correctly', async () => {
    const mockPdfText = 'Text from PDF document.';
    (pdfParse as jest.Mock).mockResolvedValue({ text: mockPdfText });
    (fs.readFile as jest.Mock).mockResolvedValue(Buffer.from('mock pdf data')); // Mock readFile for pdf-parse

    const filePath = 'path/to/mock.pdf';
    const result = await parser.parse(filePath, 'pdf');

    expect(result).toBe(mockPdfText);
    expect(fs.readFile).toHaveBeenCalledWith(path.resolve(filePath));
    expect(pdfParse).toHaveBeenCalled();
  });

  it('should parse a DOCX file correctly', async () => {
    const mockDocxText = 'Text from DOCX document.';
    (mammoth.extractRawText as jest.Mock).mockResolvedValue({ value: mockDocxText });

    const filePath = 'path/to/mock.docx';
    const result = await parser.parse(filePath, 'docx');

    expect(result).toBe(mockDocxText);
    expect(mammoth.extractRawText).toHaveBeenCalledWith({ path: path.resolve(filePath) });
  });

  it('should throw an error for an unsupported file type', async () => {
    const filePath = 'path/to/mock.unknown';
    await expect(parser.parse(filePath, 'unknown')).rejects.toThrow('Unsupported file type: unknown');
  });

  it('should handle file read errors for TXT', async () => {
    const errorMessage = 'File not found';
    (fs.readFile as jest.Mock).mockRejectedValue(new Error(errorMessage));

    const filePath = 'path/to/nonexistent.txt';
    await expect(parser.parse(filePath, 'txt')).rejects.toThrow(errorMessage);
  });

  it('should handle parsing errors for PDF', async () => {
    const errorMessage = 'PDF parsing failed';
    (fs.readFile as jest.Mock).mockResolvedValue(Buffer.from('mock pdf data'));
    (pdfParse as jest.Mock).mockRejectedValue(new Error(errorMessage));

    const filePath = 'path/to/corrupt.pdf';
    await expect(parser.parse(filePath, 'pdf')).rejects.toThrow(errorMessage);
  });
});

