import { jest } from '@jest/globals'; // Explicitly import jest
import { DocumentParser } from '../parser';
import fs from 'fs/promises';
import path from 'path';
import pdfParse from 'pdf-parse'; // This will now resolve to src/__mocks__/pdf-parse.ts
import mammoth from 'mammoth';   // This will now resolve to src/__mocks__/mammoth.ts

// Since we are mapping pdfParse and mammoth to mock files via moduleNameMapper,
// we don't need jest.mock() here for them.
// We only need to mock fs/promises manually or within the test.

describe('DocumentParser', () => {
  let parser: DocumentParser;

  beforeEach(() => {
    parser = new DocumentParser();
    // Clear all mocks before each test
    // We can cast fs.readFile to jest.Mock directly after importing jest
    (fs.readFile as unknown as jest.Mock).mockClear(); // Clear any previous mocks for fs.readFile
    (pdfParse as unknown as jest.Mock).mockClear(); // Clears mock in src/__mocks__/pdf-parse.ts
    (mammoth.extractRawText as jest.Mock).mockClear(); // Clears mock in src/__mocks__/mammoth.ts
  });

  it('should parse a TXT file correctly', async () => {
    const mockTxtContent = 'This is a test text file.';
    // Manually mock fs.readFile for this specific test
    (fs.readFile as unknown as jest.Mock).mockResolvedValue(mockTxtContent);

    const filePath = 'path/to/mock.txt';
    const result = await parser.parse(filePath, 'txt');

    expect(result).toBe(mockTxtContent);
    expect(fs.readFile).toHaveBeenCalledWith(path.resolve(filePath), 'utf-8');
  });

  it('should parse a PDF file correctly', async () => {
    const mockPdfText = 'Text from PDF document.';
    // The mock file src/__mocks__/pdf-parse.ts will handle the actual pdfParse call
    // We only need to control what the mock returns for pdfParse.
    (pdfParse as unknown as jest.Mock).mockImplementationOnce(() => Promise.resolve({ text: mockPdfText }));
    (fs.readFile as unknown as jest.Mock).mockResolvedValue(Buffer.from('mock pdf data')); // Mock readFile for pdf-parse

    const filePath = 'path/to/mock.pdf';
    const result = await parser.parse(filePath, 'pdf');

    expect(result).toBe(mockPdfText);
    expect(fs.readFile).toHaveBeenCalledWith(path.resolve(filePath));
    expect(pdfParse).toHaveBeenCalled();
  });

  it('should parse a DOCX file correctly', async () => {
    const mockDocxText = 'Text from DOCX document.';
    // The mock file src/__mocks__/mammoth.ts will handle the actual mammoth.extractRawText call
    (mammoth.extractRawText as jest.Mock).mockImplementationOnce(() => Promise.resolve({ value: mockDocxText }));

    const filePath = 'path/to/mock.docx';
    const result = await parser.parse(filePath, 'docx');

    expect(result).toBe(mockDocxText);
    expect(mammoth.extractRawText).toHaveBeenCalledWith({ path: path.resolve(filePath) });
  });

  it('should throw an error for an unsupported file type', async () => {
    const filePath = 'path/to/mock.unknown';
