import { jest } from '@jest/globals';
import OpenAI from 'openai';
import { EmbeddingModel } from '../embedding';

// Mock the OpenAI library
jest.mock('openai');

const mockOpenAI = OpenAI as jest.MockedClass<typeof OpenAI>;
const mockCreateEmbedding = jest.fn();

mockOpenAI.mockImplementation(() => ({
  embeddings: {
    create: mockCreateEmbedding,
  },
}) as unknown as OpenAI);

describe('EmbeddingModel', () => {
  let embeddingModel: EmbeddingModel;
  const mockApiKey = 'test-api-key';

  beforeEach(() => {
    mockCreateEmbedding.mockClear();
    embeddingModel = new EmbeddingModel(mockApiKey);
  });

  it('should initialize with an API key', () => {
    expect(() => new EmbeddingModel(mockApiKey)).not.toThrow();
    expect(() => new EmbeddingModel('')).toThrow('OpenAI API key is not provided.');
  });

  it('should generate an embedding for valid text', async () => {
    const mockEmbedding = [0.1, 0.2, 0.3];
    mockCreateEmbedding.mockResolvedValueOnce({
      data: [{ embedding: mockEmbedding }],
    } as any); // Type assertion to bypass strict type checking for mock

    const text = 'Hello, world!';
    const result = await embeddingModel.generateEmbedding(text);

    expect(result).toEqual(mockEmbedding);
    expect(mockCreateEmbedding).toHaveBeenCalledTimes(1);
    expect(mockCreateEmbedding).toHaveBeenCalledWith({
      model: 'text-embedding-ada-002',
      input: text,
    });
  });

  it('should return an empty array for empty text', async () => {
    const result = await embeddingModel.generateEmbedding('');
    expect(result).toEqual([]);
    expect(mockCreateEmbedding).not.toHaveBeenCalled();
  });

  it('should throw an error if embedding generation fails', async () => {
    const errorMessage = 'API error occurred';
    mockCreateEmbedding.mockRejectedValueOnce(new Error(errorMessage));

    const text = 'Some text';
    await expect(embeddingModel.generateEmbedding(text)).rejects.toThrow('Failed to generate embedding.');
    expect(mockCreateEmbedding).toHaveBeenCalledTimes(1);
  });
});

// ibe160/src/__mocks__/openai.ts
import { jest } from '@jest/globals';

const mockOpenAI = jest.fn(() => ({
  embeddings: {
    create: jest.fn(() => Promise.resolve({
      data: [{ embedding: [0.1, 0.2, 0.3] }],
    })),
  },
}));

export default mockOpenAI;

