    clearMocks: true,
    collectCoverage: false, // Set to true if coverage is needed
    // preset: 'ts-jest/presets/default-esm', // Moved custom transform to override next/jest
  };

  const clientCustomConfig = {
    ...commonConfig,
    displayName: 'client',
    testEnvironment: 'jest-environment-jsdom',
