  const commonConfig = {
    setupFilesAfterEnv: ['<rootDir>/jest.setup.js'],
    moduleNameMapper: {
      '^~/server/db$': '<rootDir>/__mocks__/server/db.ts',
      '^~/(.*)$': '<rootDir>/src/$1',
      '^@prisma/client$': '<rootDir>/src/__mocks__/@prisma/client.ts',
