import { type ChatMessage } from "~/types/chat"; // Assuming ChatMessage type will be defined globally or in a shared types file

export function generateAnswerPrompt(
  question: string,
  contextChunks: string[],
  conversationHistory: ChatMessage[] = [],
): string {
  let prompt = `You are a helpful and concise AI assistant for students. Answer the following question based ONLY on the provided context. If you cannot find the answer in the context, state that you don't know, but do not make up an answer. Keep your answer as short and direct as possible.

---
Conversation History:
`;

  if (conversationHistory.length > 0) {
    conversationHistory.forEach((message) => {
      prompt += `${message.sender}: ${message.text}\n`;
    });
  } else {
    prompt += "No previous conversation.\n";
  }

  prompt += `
---
Context:
${contextChunks.map((chunk) => `- ${chunk}`).join("\n")}

---
Question: ${question}

Answer:`;

  return prompt;
}

// src/types/chat.ts

export type ChatMessage = {
  sender: "user" | "bot";
  text: string;
};

