  '^.+\.(ts|tsx)$': ['ts-jest', {
    useESM: true,
    tsconfig: '<rootDir>/tsconfig.json',
  }],
  '^.+\.(js|jsx)$': ['babel-jest', {
    presets: [
      ['@babel/preset-env', { targets: { node: 'current' } }],
      'next/babel', // Includes @babel/preset-react
    ],
  }],
};


// Transform ignore patterns to explicitly allow transformation of certain ESM packages
