// src/app/_components/chatbot/ChatInterface.tsx
import React, { useEffect } from 'react';
import TextInput from './TextInput';
import ChatMessage from './ChatMessage';
import { ChatMessage as ChatMessageType } from '~/lib/types/chat';
import { useChatStore } from '~/lib/stores/chatStore'; // Import the Zustand store
import { v4 as uuidv4 } from 'uuid'; // Import uuid for unique keys

const ChatInterface: React.FC = () => {
  const { messages, addMessage, updateMessage, clearConversation } = useChatStore();

  const [isSendingMessage, setIsSendingMessage] = React.useState(false);

  // Initialize with a welcome message if the conversation is empty
  useEffect(() => {
    if (messages.length === 0) {
      addMessage({
        text: "Welcome to your Course-FAQ Chatbot! How can I help you today?",
        sender: "bot",
      });
    }
  }, [messages.length, addMessage]);

  const handleSendMessage = (message: string) => {
    // Add user message to the store
    addMessage({ text: message, sender: "user" });
    setIsSendingMessage(true);

    // Simulate bot response (add a loading message first)
    const botLoadingMessageId = uuidv4();
    addMessage({
      id: botLoadingMessageId,
      text: "Thinking...",
      sender: "bot",
      isLoading: true,
    });

    setTimeout(() => {
      // Update the loading message with a simulated response
      updateMessage(botLoadingMessageId, {
        text: `I'm still processing "${message}". Please wait a moment.`,
        isLoading: false,
      });
      setIsSendingMessage(false);
    }, 1500);
  };

  return (
    <div data-testid="chat-interface-container" className="flex flex-col h-full bg-gray-50 dark:bg-gray-950 rounded-lg shadow-md p-4">
      {/* Header/Title - Optional, can be part of page.tsx */}
      {/* <h2 className="text-xl font-semibold mb-4 text-gray-800 dark:text-gray-100">Your Chat</h2> */}

      {/* Main chat messages display area */}
      <div data-testid="messages-display-area" className="flex-1 overflow-y-auto p-4 space-y-4 bg-white dark:bg-gray-800 rounded-lg shadow-inner">
        {messages.map((msg) => (
          <ChatMessage key={msg.id} message={msg} />
        ))}
      </div>

      {/* Chat input area */}
      <div className="mt-4">
        <TextInput onSendMessage={handleSendMessage} isLoading={isSendingMessage} />
      </div>
    </div>
  );
};

export default ChatInterface;

