// src/server/api/routers/chatbot.ts
import { z } from "zod";
import { createTRPCRouter, publicProcedure } from "../trpc";
import { EmbeddingModel } from "~/server/services/embedding";
import { PineconeService } from "~/server/services/pinecone";
import { env } from "~/env";
import { generateAnswerPrompt } from "~/server/prompts/answerGenerationPrompt";
import { LLMClient } from "~/server/services/llm"; // Import LLMClient
import { type ChatMessage } from "~/lib/types/chat"; // Import ChatMessage type

const embeddingModel = new EmbeddingModel();
const pineconeService = new PineconeService(
  env.PINECONE_API_KEY,
  env.PINECONE_ENVIRONMENT,
  env.PINECONE_INDEX_NAME
);
const llmClient = new LLMClient(); // Instantiate LLMClient

// Initialize Pinecone index once
void pineconeService.initializeIndex().catch(e => console.error("Failed to initialize Pinecone index:", e));

export const chatbotRouter = createTRPCRouter({
  queryChatbot: publicProcedure
    .input(z.object({
      message: z.string(),
      conversationHistory: z.array(z.object({
        sender: z.enum(["user", "bot"]),
        text: z.string(),
      })).optional(),
    }))
    .query(async ({ ctx, input }) => {
      const userEmbedding = await embeddingModel.generateEmbedding(input.message);
      const relevantChunks = await pineconeService.queryVectors(userEmbedding);

      const llmPrompt = generateAnswerPrompt(
        input.message,
        relevantChunks, // Pass relevantChunks directly
        input.conversationHistory || []
      );

      const llmResponse = await llmClient.generateResponse(llmPrompt); // Generate response with LLM

      // Parse LLM response to extract answer and sources
      const sourceRegex = /Source: (.*?)(, Page (\d+))?(\s+\((https?:\/\/[^\)]+)\))?$/gm;
      let match;
      const sources: ChatMessage['sources'] = [];
      let answer = llmResponse; // Assume full response is answer, then trim sources

      const lines = llmResponse.split('\n').reverse(); // Process from bottom up to find sources

      for (const line of lines) {
        match = sourceRegex.exec(line);
        if (match) {
          const documentName = match[1].trim();
          const pageNumber = match[3] ? parseInt(match[3]) : undefined;
          const url = match[5];
          sources.unshift({ documentName, pageNumber, url }); // Add to beginning to maintain order
          answer = answer.replace(line, '').trim(); // Remove source line from answer
        } else {
          // Stop if a non-source line is encountered (assuming sources are at the end)
          break;
        }
      }

      // 5. Return the answer and sources
      console.log("User embedding:", userEmbedding);
      console.log("Relevant chunks:", relevantChunks);
      console.log("LLM Prompt:", llmPrompt);
      console.log("LLM Response:", llmResponse);
      return { answer: answer.trim(), sources };
    }),
});

// src/app/_components/chatbot/ChatMessage.tsx
import React from 'react';
import { ChatMessage as ChatMessageType } from '~/lib/types/chat';

interface ChatMessageProps {
  message: ChatMessageType;
}

const ChatMessage: React.FC<ChatMessageProps> = ({ message }) => {
  const isUser = message.sender === 'user';
  const messageClasses = isUser
    ? 'bg-purple-600 text-white self-end'
    : 'bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-gray-100 self-start';
  const containerClasses = isUser ? 'justify-end' : 'justify-start';

  if (message.isLoading) {
    return (
      <div className={`flex ${containerClasses} mb-2`}>
        <div className={`${messageClasses} p-3 rounded-lg max-w-xs shadow-md`}>
          <div className="flex space-x-1">
            <span className="sr-only">Loading...</span>
            <div className="h-2 w-2 bg-white dark:bg-gray-300 rounded-full animate-bounce [animation-delay:-0.3s]"></div>
            <div className="h-2 w-2 bg-white dark:bg-gray-300 rounded-full animate-bounce [animation-delay:-0.15s]"></div>
            <div className="h-2 w-2 bg-white dark:bg-gray-300 rounded-full animate-bounce"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className={`flex ${containerClasses} mb-2`}>
      <div className={`${messageClasses} p-3 rounded-lg max-w-xs shadow-md`}>
        <p className="whitespace-pre-wrap">{message.text}</p>
        {message.sources && message.sources.length > 0 && (
          <div className="mt-2 text-xs opacity-75">
            <strong>Sources:</strong>
            <ul className="list-disc list-inside">
              {message.sources.map((src, index) => (
                <li key={index}>
                  {src.documentName}
                  {src.pageNumber && `, Page ${src.pageNumber}`}
                  {src.url && (
                    <>
                      {" "}
                      (<a href={src.url} target="_blank" rel="noopener noreferrer" className="text-blue-300 hover:underline">{new URL(src.url).hostname}</a>)
                    </>
                  )}
                </li>
              ))}
            </ul>
          </div>
        )}
        {message.isError && (
          <p className="mt-2 text-sm font-semibold text-red-500">Error processing message.</p>
        )}
      </div>
    </div>
  );
};

export default ChatMessage;

import { type ChatMessage } from "~/types/chat";
import { type RecordMetadata } from '@pinecone-database/pinecone'; // Import RecordMetadata

export function generateAnswerPrompt(
  question: string,
  relevantChunks: Array<{ id: string; score: number; metadata: RecordMetadata }>,
  conversationHistory: ChatMessage[] = [],
): string {
  let prompt = `You are a helpful and concise AI assistant for students. Answer the following question based ONLY on the provided context. If you cannot find the answer in the context, state that you don't know, but do not make up an answer.
Your answer MUST include citations to the source documents in the format "Source: [Document Name], [Page Number (if available)]". If a URL is available, also include it as a clickable link.
Keep your answer as short and direct as possible.

---
Conversation History:
`;

  if (conversationHistory.length > 0) {
    conversationHistory.forEach((message) => {
      prompt += `${message.sender}: ${message.text}\n`;
    });
  } else {
    prompt += "No previous conversation.\n";
  }

  // Extract context content and create source citations
  const contextAndSources = relevantChunks.map(chunk => {
    const text = (chunk.metadata as { text: string }).text || "";
    const documentName = (chunk.metadata as { documentName?: string }).documentName || "Unknown Document";
    const pageNumber = (chunk.metadata as { pageNumber?: number }).pageNumber;
    const url = (chunk.metadata as { url?: string }).url;

    let source = `Source: ${documentName}`;
    if (pageNumber) {
      source += `, Page ${pageNumber}`;
    }
    if (url) {
      source += ` (${url})`;
    }

    return `${text}\n(${source})`;
  }).join("\n\n");


  prompt += `
---
Context (with sources):
${contextAndSources}

---
Question: ${question}

Answer:`;

  return prompt;
}

