import { jest } from '@jest/globals';
import { appRouter } from '~/server/api/root';
import { createCallerFactory } from '~/server/api/trpc';
import { languageService } from '~/server/services/language';
import OpenAI from 'openai';

// Mock the dependencies
jest.mock('~/server/services/language');
jest.mock('openai');

const createCaller = createCallerFactory(appRouter);

describe('chatbotRouter.queryChatbot', () => {
  let caller: ReturnType<typeof createCaller>;
  let mockDetectLanguage: jest.Mock;
  let mockCreateChatCompletion: jest.Mock;

  beforeEach(() => {
    // Set up the caller for each test
    caller = createCaller({
      session: null,
      db: {} as any, // Mock db if needed
    });

    // Mock languageService implementation
    mockDetectLanguage = jest.fn();
    (languageService as any).detectLanguage = mockDetectLanguage;

    // Mock OpenAI implementation
    mockCreateChatCompletion = jest.fn();
    (OpenAI.prototype.chat.completions.create as jest.Mock) = mockCreateChatCompletion;
  });

  afterEach(() => {
    jest.clearAllMocks();
  });

  it('should detect language and call LLM with a language-specific system prompt', async () => {
    // Arrange
    const testMessage = 'Hva er en-til-en-undervisning?';
    const detectedLanguage = 'norsk';
    const expectedResponse = 'Eins-zu-eins-Unterricht ist eine personalisierte Lernmethode.';

    mockDetectLanguage.mockResolvedValue(detectedLanguage);
    mockCreateChatCompletion.mockResolvedValue({
      choices: [{ message: { content: expectedResponse } }],
    });

    // Act
    const result = await caller.chatbot.queryChatbot({ message: testMessage });

    // Assert
    expect(mockDetectLanguage).toHaveBeenCalledWith(testMessage);
    expect(mockCreateChatCompletion).toHaveBeenCalledWith({
      model: 'gpt-3.5-turbo',
      messages: [
        {
          role: 'system',
          content: `You are a helpful assistant for the IBE160 course. Please respond to the user in the same language they used, which has been detected as: ${detectedLanguage}.`,
        },
        { role: 'user', content: testMessage },
      ],
    });
    expect(result.answer).toBe(expectedResponse);
  });

  it('should handle undetermined language gracefully', async () => {
    // Arrange
    const testMessage = '12345';
    const detectedLanguage = 'und'; // 'und' for undetermined
    const expectedResponse = 'The language was undetermined.';

    mockDetectLanguage.mockResolvedValue(detectedLanguage);
    mockCreateChatCompletion.mockResolvedValue({
      choices: [{ message: { content: expectedResponse } }],
    });

    // Act
    const result = await caller.chatbot.queryChatbot({ message: testMessage });

    // Assert
    expect(mockDetectLanguage).toHaveBeenCalledWith(testMessage);
    expect(mockCreateChatCompletion).toHaveBeenCalledWith(
      expect.objectContaining({
        messages: expect.arrayContaining([
          expect.objectContaining({
            content: expect.stringContaining(detectedLanguage),
          }),
        ]),
      })
    );
    expect(result.answer).toBe(expectedResponse);
  });
});

