import { franc } from 'franc';

/**
 * A service class to handle language detection and translation.
 */
export class LanguageService {
  /**
   * Detects the language of a given text.
   *
   * @param text The text to analyze.
   * @returns A promise that resolves to the three-letter ISO 639-3 language code,
   *          or 'und' if the language cannot be determined.
   */
  async detectLanguage(text: string): Promise<string> {
    // franc returns the ISO 639-3 code or 'und' if it's undetermined.
    const langCode = franc(text);
    return Promise.resolve(langCode);
  }

  /**
   * Translates text from a source language to a target language.
   *
   * @param text The text to translate.
   * @param targetLanguage The language to translate the text into.
   * @param sourceLanguage The language of the source text (optional).
   * @returns A promise that resolves to the translated text.
   * @throws Not implemented yet.
   */
  async translate(text: string, targetLanguage: string, sourceLanguage?: string): Promise<string> {
    // Placeholder for translation logic. This would likely involve a third-party API.
    throw new Error('Translation functionality is not implemented yet.');
  }
}

export const languageService = new LanguageService();

import { z } from "zod";
import OpenAI from 'openai';

import {
  createTRPCRouter,
  publicProcedure,
} from "~/server/api/trpc";
import { languageService } from "~/server/services/language";
import { env } from "~/env";

const openai = new OpenAI({
  apiKey: env.OPENAI_API_KEY,
});

export const chatbotRouter = createTRPCRouter({
  queryChatbot: publicProcedure
    .input(
      z.object({
        message: z.string(),
        conversationHistory: z.array(z.object({ 
            sender: z.enum(['user', 'bot']), 
            text: z.string() 
        })).optional(),
      })
    )
    .query(async ({ ctx, input }) => {
      const { message } = input;

      // 1. Detect the language of the user's query
      const detectedLanguage = await languageService.detectLanguage(message);

      // 2. Placeholder for embedding generation and vector search.
      // This is where the retrieval-augmented generation (RAG) would happen.
      // For now, we'll just use the detected language to format the prompt.

      // 3. Construct the prompt for the LLM
      const systemPrompt = `You are a helpful assistant for the IBE160 course. Please respond to the user in the same language they used, which has been detected as: ${detectedLanguage}.`;

      // 4. Call the LLM
      try {
        const response = await openai.chat.completions.create({
          model: "gpt-3.5-turbo", // Or another suitable model
          messages: [
            { role: "system", content: systemPrompt },
            { role: "user", content: message },
          ],
        });

        const answer = response.choices[0]?.message?.content ?? "Sorry, I could not generate a response.";

        return {
          answer,
          sources: [], // Placeholder for actual sources from RAG
        };
      } catch (error) {
        console.error('Error calling OpenAI:', error);
        throw new Error('Failed to get a response from the chatbot.');
      }
    }),
});

import { jest } from '@jest/globals';
import { appRouter } from '~/server/api/root';
import { createCallerFactory } from '~/server/api/trpc';
import { languageService } from '~/server/services/language';
import OpenAI from 'openai';

// Mock the dependencies
jest.mock('~/server/services/language');
jest.mock('openai');

const createCaller = createCallerFactory(appRouter);

describe('chatbotRouter.queryChatbot', () => {
  let caller: ReturnType<typeof createCaller>;
  let mockDetectLanguage: jest.Mock;
  let mockCreateChatCompletion: jest.Mock;

  beforeEach(() => {
    // Set up the caller for each test
    caller = createCaller({
      session: null,
      db: {} as any, // Mock db if needed
    });

    // Mock languageService implementation
    mockDetectLanguage = jest.fn();
    (languageService as any).detectLanguage = mockDetectLanguage;

    // Mock OpenAI implementation
    mockCreateChatCompletion = jest.fn();
    (OpenAI.prototype.chat.completions.create as jest.Mock) = mockCreateChatCompletion;
  });

  afterEach(() => {
    jest.clearAllMocks();
  });

  it('should detect language and call LLM with a language-specific system prompt', async () => {
    // Arrange
    const testMessage = 'Hva er en-til-en-undervisning?';
    const detectedLanguage = 'norsk';
    const expectedResponse = 'Eins-zu-eins-Unterricht ist eine personalisierte Lernmethode.';

    mockDetectLanguage.mockResolvedValue(detectedLanguage);
    mockCreateChatCompletion.mockResolvedValue({
      choices: [{ message: { content: expectedResponse } }],
    });

    // Act
    const result = await caller.chatbot.queryChatbot({ message: testMessage });

    // Assert
    expect(mockDetectLanguage).toHaveBeenCalledWith(testMessage);
    expect(mockCreateChatCompletion).toHaveBeenCalledWith({
      model: 'gpt-3.5-turbo',
      messages: [
        {
          role: 'system',
          content: `You are a helpful assistant for the IBE160 course. Please respond to the user in the same language they used, which has been detected as: ${detectedLanguage}.`,
        },
        { role: 'user', content: testMessage },
      ],
    });
    expect(result.answer).toBe(expectedResponse);
  });

  it('should handle undetermined language gracefully', async () => {
    // Arrange
    const testMessage = '12345';
    const detectedLanguage = 'und'; // 'und' for undetermined
    const expectedResponse = 'The language was undetermined.';

    mockDetectLanguage.mockResolvedValue(detectedLanguage);
    mockCreateChatCompletion.mockResolvedValue({
      choices: [{ message: { content: expectedResponse } }],
    });

    // Act
    const result = await caller.chatbot.queryChatbot({ message: testMessage });

    // Assert
    expect(mockDetectLanguage).toHaveBeenCalledWith(testMessage);
    expect(mockCreateChatCompletion).toHaveBeenCalledWith(
      expect.objectContaining({
        messages: expect.arrayContaining([
          expect.objectContaining({
            content: expect.stringContaining(detectedLanguage),
          }),
        ]),
      })
    );
    expect(result.answer).toBe(expectedResponse);
  });
});

