// src/server/api/routers/chatbot.ts
import { z } from "zod";
import { createTRPCRouter, publicProcedure } from "../trpc";
import { EmbeddingModel } from "~/server/services/embedding";
import { PineconeService } from "~/server/services/pinecone";
import { env } from "~/env";
import { generateAnswerPrompt } from "~/server/prompts/answerGenerationPrompt";
import { LLMClient } from "~/server/services/llm"; // Import LLMClient
import { type ChatMessage } from "~/lib/types/chat"; // Import ChatMessage type

const embeddingModel = new EmbeddingModel();
const pineconeService = new PineconeService(
  env.PINECONE_API_KEY,
  env.PINECONE_ENVIRONMENT,
  env.PINECONE_INDEX_NAME
);
const llmClient = new LLMClient(); // Instantiate LLMClient

// Initialize Pinecone index once
void pineconeService.initializeIndex().catch(e => console.error("Failed to initialize Pinecone index:", e));

export const chatbotRouter = createTRPCRouter({
  queryChatbot: publicProcedure
    .input(z.object({
      message: z.string(),
      conversationHistory: z.array(z.object({
        sender: z.enum(["user", "bot"]),
        text: z.string(),
      })).optional(),
    }))
    .query(async ({ ctx, input }) => {
      const userEmbedding = await embeddingModel.generateEmbedding(input.message);
      const relevantChunks = await pineconeService.queryVectors(userEmbedding);

      const llmPrompt = generateAnswerPrompt(
        input.message,
        relevantChunks, // Pass relevantChunks directly
        input.conversationHistory || []
      );

      const llmResponse = await llmClient.generateResponse(llmPrompt); // Generate response with LLM

      // Parse LLM response to extract answer and sources
      const sourceRegex = /Source: (.*?)(, Page (\d+))?(\s+\((https?:\/\/[^\)]+)\))?$/gm;
      let match;
      const sources: ChatMessage['sources'] = [];
      let answer = llmResponse; // Assume full response is answer, then trim sources

      const lines = llmResponse.split('\n').reverse(); // Process from bottom up to find sources

      for (const line of lines) {
        match = sourceRegex.exec(line);
        if (match) {
          const documentName = match[1].trim();
          const pageNumber = match[3] ? parseInt(match[3]) : undefined;
          const url = match[5];
          sources.unshift({ documentName, pageNumber, url }); // Add to beginning to maintain order
          answer = answer.replace(line, '').trim(); // Remove source line from answer
        } else {
          // Stop if a non-source line is encountered (assuming sources are at the end)
          break;
        }
      }

      // 5. Return the answer and sources
      console.log("User embedding:", userEmbedding);
      console.log("Relevant chunks:", relevantChunks);
      console.log("LLM Prompt:", llmPrompt);
      console.log("LLM Response:", llmResponse);
      return { answer: answer.trim(), sources };
    }),
});

import { Pinecone, Index, RecordMetadata } from '@pinecone-database/pinecone';

// NOTE: You will need to install the Pinecone client library:
// npm install @pinecone-database/pinecone

export class PineconeService {
  private pinecone: Pinecone;
  private index: Index<RecordMetadata> | undefined;
  private indexName: string;

  constructor(apiKey: string, environment: string, indexName: string) {
    this.pinecone = new Pinecone({
      apiKey,
      environment,
    });
    this.indexName = indexName;
  }

  public async initializeIndex(): Promise<void> {
    try {
      const describeIndexShow = await this.pinecone.describeIndex(this.indexName);
      this.index = this.pinecone.Index(this.indexName);
      console.log(`Connected to existing Pinecone index: ${this.indexName}`);
    } catch (error: any) {
      if (error.status === 404) {
        // Index does not exist, create it.
        // NOTE: You might want to configure the dimension and metric based on your embedding model
        // For 'text-embedding-ada-002', the dimension is 1536.
        // A common metric is 'cosine'.
        await this.pinecone.createIndex({
          name: this.indexName,
          dimension: 1536, // Example dimension for OpenAI's text-embedding-ada-002
          metric: 'cosine',
          // You might also need to specify a pod_type and replicas for production
        });
        this.index = this.pinecone.Index(this.indexName);
        console.log(`Created and connected to new Pinecone index: ${this.indexName}`);
      } else {
        console.error("Error initializing Pinecone index:", error);
        throw new Error("Failed to initialize Pinecone index.");
      }
    }
  }

  public async upsertVectors(vectors: Array<{ id: string; values: number[]; metadata: RecordMetadata }>): Promise<void> {
    if (!this.index) {
      throw new Error("Pinecone index not initialized. Call initializeIndex() first.");
    }
    try {
      await this.index.upsert(vectors);
      console.log(`Successfully upserted ${vectors.length} vectors to Pinecone.`);
    } catch (error) {
      console.error("Error upserting vectors to Pinecone:", error);
      throw new Error("Failed to upsert vectors to Pinecone.");
    }
  }

  public async queryVectors(vector: number[], topK: number = 5): Promise<Array<{ id: string; score: number; metadata: RecordMetadata }>> {
    if (!this.index) {
      throw new Error("Pinecone index not initialized. Call initializeIndex() first.");
    }
    try {
      const queryResult = await this.index.query({
        vector,
        topK,
        includeMetadata: true,
      });
      return queryResult.matches || [];
    } catch (error) {
      console.error("Error querying vectors from Pinecone:", error);
      throw new Error("Failed to query vectors from Pinecone.");
    }
  }
}

