import React from 'react';
import { render, screen, fireEvent, waitFor } from '@testing-library/react';
import '@testing-library/jest-dom';
import { useRouter, useSearchParams } from 'next/navigation';
import { signIn } from 'next-auth/react'; // This will be the globally mocked signIn
import LoginPage from './page'; // Assuming LoginPage component is exported from page.tsx

const mockPush = jest.fn();

describe('LoginPage', () => { // Start of the main describe block
  beforeEach(() => {
    (useRouter as jest.Mock).mockReturnValue({
      push: mockPush,
      replace: jest.fn(),
      // Add other methods as needed
    });
    (useSearchParams as jest.Mock).mockReturnValue(new URLSearchParams());
    // Clear mocks before each test
    (signIn as jest.Mock).mockClear(); // Directly clear the globally mocked signIn
    mockPush.mockClear();
  });

  afterEach(() => {
    jest.clearAllMocks();
  });




  it('should display the login form', () => {
    render(<LoginPage />);
    expect(screen.getByRole('heading', { name: /login/i })).toBeInTheDocument();
    expect(screen.getByLabelText(/email/i)).toBeInTheDocument();
    expect(screen.getByLabelText(/password/i)).toBeInTheDocument();
    expect(screen.getByRole('button', { name: 'Login' })).toBeInTheDocument();
    expect(screen.getByText(/don't have an account\?/i)).toBeInTheDocument();
    expect(screen.getByRole('link', { name: /register here/i })).toBeInTheDocument();
