// src/server/webhooks/supabase-storage-trigger.ts

import { NextApiRequest, NextApiResponse } from 'next';
import { env } from '~/env'; // Assuming env for SUPABASE_WEBHOOK_SECRET
import { processDocument } from '~/server/ingestion'; // Placeholder for Story 2.2 ingestion logic
import { NotificationService } from '~/server/services/notification'; // Placeholder for notification service

// Placeholder for NotificationService - will be implemented in a later step
const notificationService = new NotificationService();

export default async function handler(req: NextApiRequest, res: NextApiResponse) {
  if (req.method !== 'POST') {
    return res.status(405).json({ message: 'Method Not Allowed' });
  }

  // Validate Supabase event header
  const supabaseEvent = req.headers['x-supabase-event'] as string;
  if (!supabaseEvent || supabaseEvent !== 'STORAGE_OBJECT_CREATED') {
    return res.status(400).json({ message: 'Invalid Supabase event type' });
  }

  // Optional: Add a secret key check for webhook security
  // const secret = req.headers['x-supabase-webhook-secret'] as string;
  // if (secret !== env.SUPABASE_WEBHOOK_SECRET) {
  //   return res.status(401).json({ message: 'Unauthorized' });
  // }

  const { bucketId, objectId, name: filePath } = req.body;

  if (!bucketId || !objectId || !filePath) {
    return res.status(400).json({ message: 'Missing required fields in payload' });
  }

  console.log(`Received Supabase storage event: ${supabaseEvent} for file: ${filePath}`);

  try {
    // Invoke the document processing pipeline from Story 2.2
    // Assuming processDocument takes the file path and bucketId to download and process
    await processDocument(bucketId, filePath);

    // Notify teacher of successful processing
    // Assuming we can get teacher's email from context or bucketId
    // For now, just log success
    console.log(`Successfully processed document: ${filePath}`);
    // await notificationService.sendEmail(teacherEmail, 'Document Processed', `Your document ${filePath} has been successfully processed.`);

    res.status(200).json({ message: 'Document processing initiated successfully' });
  } catch (error) {
    console.error(`Error processing document ${filePath}:`, error);

    // Notify teacher of processing failure
    // await notificationService.sendEmail(teacherEmail, 'Document Processing Failed', `There was an error processing your document ${filePath}.`);

    res.status(500).json({ message: 'Failed to initiate document processing', error: (error as Error).message });
  }
}

// Placeholder for Story 2.2 ingestion logic (will be assumed to exist and be functional)
// In a real scenario, this would be imported from src/server/ingestion.ts
// For now, a mock for development
/*
// src/server/ingestion.ts
export async function processDocument(bucketId: string, filePath: string) {
  console.log(`Mocking document processing for bucket: ${bucketId}, file: ${filePath}`);
  // Simulate download, parsing, embedding, and Pinecone storage
  await new Promise(resolve => setTimeout(resolve, 2000)); // Simulate async work
  console.log(`Mock processing complete for ${filePath}`);
}
*/

// Placeholder for NotificationService (will be implemented in src/server/services/notification.ts)
/*
// src/server/services/notification.ts
export class NotificationService {
  async sendEmail(recipient: string, subject: string, body: string): Promise<void> {
    console.log(`Mock sending email to ${recipient} with subject: ${subject}`);
    console.log(`Body: ${body}`);
    // In a real implementation, use Resend or similar service
    await new Promise(resolve => setTimeout(resolve, 1000));
  }
}
*/

